    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       try
        {
            DbConnect.Connect();
            String query = "select RollNumber,Name from Student where RollNumber = '"+jTextField1.getText()+"';";
            ResultSet rs = DbConnect.stmt.executeQuery(query);
            if (rs.next())
            {
                jTextField2.setText(rs.getString(2));
                jButton2.setEnabled(true);
            }
            else
            {
                jTextField2.setText("NOT FOUND");
                jButton2.setEnabled(false);
            }
            DbConnect.Disconnect();
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(this,e.getMessage());
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        try
        {
            DbConnect.Connect();
            String query = "delete from Student where RollNumber = "+jTextField1.getText()+";";
            String query1 = "Select UserId from Student where RollNumber = "+jTextField1.getText()+";";
            ResultSet rs = DbConnect.stmt.executeQuery(query1);
            String userID;
            rs.next();
            userID = rs.getString(1);
            String query2 = "delete from Admintable where UserID = '"+userID+"';";
            String query3 = "delete from takes where RollNumber = '"+jTextField1.getText()+"';";
            
            int n = JOptionPane.showConfirmDialog(this,"Want to delete this item ?!");
            if (n==0)
            {
                DbConnect.stmt.executeUpdate(query3);
                DbConnect.stmt.executeUpdate(query);
                DbConnect.stmt.executeUpdate(query2);
                JOptionPane.showMessageDialog(this,"Deleted Successfully !!!");
                jTextField1.setText("");
                jTextField2.setText("");
                jButton2.setEnabled(false);
            }
                
        }
        catch(Exception e)
        {
            JOptionPane.showMessageDialog(this,e.getMessage());
        }
    }//GEN-LAST:event_jButton2ActionPerformed

